css:
.header {
    display: flex;
    display: -webkit-box;
    justify-content: justify;
    -webkit-box-pack: justify;
    border-bottom: 1px solid #dddddd;
    padding-bottom: 4px;
}

.info {
    display: flex;
    display: -webkit-box;
    justify-content: justify;
    -webkit-box-pack: justify;
    border-bottom: 1px solid #dddddd;
    padding-bottom: 2px;

}

html:
{% set company = frappe.db.get_value("Company", doc.company, ["*"], as_dict = true) %}
{% set letter_head = frappe.db.get_value("Letter Head", company.default_letter_head, ["image"], as_dict=true) %}
{% set links = frappe.get_all('Dynamic Link', filters={'link_doctype': 'Company', 'link_name': doc.company, 'parenttype': 'Address'}, fields=['parent']) %}
{% if links %}
{% set address = frappe.db.get_value("Address", links[0].parent, ["*"], as_dict=true) %}
{% endif %}

{% set patient = frappe.db.get_value("Patient", doc.patient, ["*"], as_dict=true) %}
{% set practitioner = frappe.db.get_value("Healthcare Practitioner", doc.practitioner, ["*"], as_dict=true) %}
{% set dob = frappe.utils.getdate(patient.dob) %} {# Replace with actual DOB #}
{% set today = frappe.utils.nowdate() %}
{% set birth_date = frappe.utils.getdate(dob) %}
{% set current_date = frappe.utils.getdate(today) %}
{% set age = current_date.year - birth_date.year - ((current_date.month, current_date.day) < (birth_date.month, birth_date.day)) %}
{% set mrOrMrs = "Mr." if patient.gender == "Male" else "Mrs."%}
{% set heOrShe = "he" if patient.gender == "Male" else "she"%}
{% set hisOrHer = "his" if patient.gender == "Male" else "her"%}
{% macro header() %}
<div class="header">
    <div class="">
        <img src="{{letter_head.image}}"  style="width:100px; margin-bottom:32px;" />

    </div>
    <div style="text-align: center">
        <h1>Consultation</h1>
        <p>{{doc.name}}</p>
    </div>

    <div style="text-align: right;">
        {% if address.address_title %}
            <p style="font-weight: bold; font-size: large;">{{address.address_title}}<p/>
        {% endif %}
        {% if address.address_line1 %}
            <p>{{address.address_line1}}</p>
        {% endif %}
        {% if address.city %}
            <p>{{address.city}}</p>
        {% endif %}
        {% if address.pincode %}
            <p>{{address.pincode}}</p>
        {% endif %}
        <p>Tel: 4908128</p>
        <p>BRN: C22188669</p>
    </div>

</div>
{% endmacro %}

{% macro info() %}
<div class="info">
    <div class="col-6">
        <section class="patient-info">

            <p><strong>Name:</strong> {{ patient.patient_name }}</p>
            <p><strong>Patient HIS:</strong> {{ doc.patient }}</p>

            <p>
            {% if patient.dob %}
                <strong>D.O.B:</strong> {{ frappe.utils.formatdate(patient.dob) }}  ({{age}} yrs) 
            {% endif %}
            ({{ "M" if  patient.sex == "Male" else "F"  }})
            </p>
            {% if patient.address %}
            <p><strong>Address:</strong> </p>
            <p style="white-space-collapse: preserve; max-width:200px">{{ patient.address }}</p>
            {% endif %}
            <p><strong>Tel:</strong> {{ patient.mobile }}</p>
            {%if patient.uid %}
                <p><strong>NID:</strong> {{ patient.uid }}</p>
            {% endif %}
        </section>
    </div>
    <div class="col-">
        {% if doc.practitioner %}
            <section class="patient-info">
                <div style="height: 45px;"></div>
                <p><strong>Referring Practitioner:</strong> {{ practitioner.name }}</p>
                <p>{{frappe.format_date(doc.creation)}}</p>
            </section>
        {% endif %}

    </div>
</div>
{% endmacro %}



<div>
    {{header()}}
    {{info()}}

    {% if doc.chief_complaint %}
    <h4>Chief Complaint</h4>
    <p>{{doc.chief_complaint}}</p>
    {% endif %}

    {% if doc.past_history %}
    <h4>Past History / Allergies</h4>
    <p>{{doc.past_history}}</p>
    {% endif %}

    {% if doc.clinical_examination %}
    <h4>Clinical Examination </h4>
    <p>{{doc.clinical_examination}}</p>
    {% endif %}

    {% if doc.investigation_done %}
    <h4>Investigation Done</h4>
    <p>{{doc.investigation_done}}</p>
    {% endif %}

    {% if doc.provisional_diagnosis %}
    <h4>Provisional Diagnosis</h4>
    <p>{{doc.provisional_diagnosis}}</p>
    {% endif %}

    {% if doc.plan %}
    <h4>Plan</h4>
    <p>{{doc.plan}}</p>
    {% endif %}
    <p class="mt-24 text-right"> {{ practitioner.name }}</p>

</div>
